// Get the incoming data from the Gemini node
const geminiResponse = $input.item.json;

// Extract the text string containing the report
const reportString = geminiResponse.content.parts[0].text;

// Parse the string into a JSON object
const parsedReport = JSON.parse(reportString);

// Return the parsed object for the next node
return { json: parsedReport };

// Get the parsed report from the previous node
const report = $input.item.json;

// This array will hold each line of our Markdown report
const markdownLines = [];

// --- Build Header and Summary ---
markdownLines.push(# ðŸ›¡ ${report.reportTitle});
markdownLines.push(\n## Executive Summary);
markdownLines.push(> ${report.executiveSummary.replace(/\n/g, '\n> ')}); // Add blockquote to each line
markdownLines.push(\n---);

// --- Build Scope ---
markdownLines.push(\n## Scope of Assessment);
markdownLines.push(* **Target IP:** \${report.scopeOfAssessment.targetIp}\`);
markdownLines.push(* *Scan Type:* ${report.scopeOfAssessment.scanType});
markdownLines.push(* *Scan Date (UTC):* ${report.scopeOfAssessment.scanDateTimeUTC});
markdownLines.push(* *Scanner Version:* Nmap ${report.scopeOfAssessment.scannerVersion});
markdownLines.push(\n---);

// --- Build Vulnerabilities Section ---
markdownLines.push(\n## ðŸš¨ Identified Services & Vulnerabilities`);
for (const service of report.identifiedServicesAndVulnerabilities) {
  markdownLines.push(\n### Port ${service.portId}/${service.protocol} (${service.serviceName}) - ${service.product || 'Unknown'} ${service.version || ''});
  markdownLines.push(* **Service Description:** ${service.commonFunctionDescription});

  if (service.vulnerabilities && service.vulnerabilities.length > 0) {
    for (const vuln of service.vulnerabilities) {
      let riskEmoji = '';
      if (vuln.riskRating === 'Critical') riskEmoji = 'ðŸ”´';
      if (vuln.riskRating === 'High') riskEmoji = 'ðŸŸ ';
      if (vuln.riskRating === 'Medium') riskEmoji = 'ðŸŸ¡';

      markdownLines.push(\n#### Vulnerability: ${vuln.name});
      markdownLines.push(* **Risk Rating:** **${vuln.riskRating}** ${riskEmoji});
      if (vuln.cveId) {
        markdownLines.push(* **CVE:** [${vuln.cveId}](https://nvd.nist.gov/vuln/detail/${vuln.cveId}));
      } else {
        markdownLines.push(* **CVE:** N/A);
      }
      markdownLines.push(> ${vuln.description});
    }
  }
  markdownLines.push(\n***);
}

// --- Build Recommendations Section ---
markdownLines.push(\n## âœ… Remediation Recommendations);
for (const rec of report.remediationRecommendations) {
    let priorityEmoji = '';
    if (rec.priority === 'Critical') priorityEmoji = 'ðŸ”´';
    if (rec.priority === 'High') priorityEmoji = 'ðŸŸ ';
    if (rec.priority === 'Medium') priorityEmoji = 'ðŸŸ¡';

    markdownLines.push(\n### ${rec.recommendationTitle});
    markdownLines.push(* **Priority:** **${rec.priority}** ${priorityEmoji});
    const appliesTo = Array.isArray(rec.appliesTo) ? rec.appliesTo.join(', ') : rec.appliesTo;
    markdownLines.push(* **Applies To:** ${appliesTo});
    markdownLines.push(\n**Detailed Steps:**\n${rec.detailedSteps});
    if (rec.bestPractices && rec.bestPractices.length > 0) {
        markdownLines.push(\n**Best Practices:**);
        rec.bestPractices.forEach(bp => markdownLines.push(* ${bp}));
    }
}
markdownLines.push(\n---);

// --- Build Conclusion ---
markdownLines.push(\n## Conclusion);
markdownLines.push(report.conclusion);

// --- Final Output ---
// Join all lines into a single string
const finalMarkdown = markdownLines.join('\n');

// Return the markdown in a new field for the next node to use
return {
  json: {
    markdownReport: finalMarkdown
  }
};
